UPDATE hotel SET idioma_id = 1, respetar_cupo = 0, contenido_html = NULL, booking_onrequest = 0;UPDATE promocion SET inicio = NULL, fin = NULL WHERE hotel_id IS NOT NULL;DELETE FROM hotel_condicion WHERE 1;INSERT INTO hotel_condicion(hotel_id, condicion_id) SELECT hotel.id, condicion.idFROM  `hotel` JOIN condicionWHERE condicion.id = ( SELECT c.idFROM condicion AS cWHERE c.nombre LIKE  "%booking conditions%"LIMIT 0 , 1 );